let servicesItems=document.querySelectorAll(".services__item"),mainNavToggle=document.querySelector(".main-nav__toggle"),mainNavList=document.querySelector(".main-nav__list");function addListener(e){e.addEventListener("click",s=>{if(e.classList.contains("services__item--shown"))e.classList.remove("services__item--shown");else{let s=document.querySelector(".services__item--shown");null!=s&&s.classList.remove("services__item--shown"),e.classList.add("services__item--shown")}})}mainNavToggle.addEventListener("click",e=>{e.preventDefault(),mainNavList.classList.toggle("main-nav__list--shown")});for(let e=0;e<servicesItems.length;e++)addListener(servicesItems[e]);